sp_header_footer.client_script:
api.controller = function($scope, $window, $location,$http) {
	/* widget controller */
	var c = this;
	var table = "x_1093559_hsc_em_0_hsc_footer";

	c.fetchRecords = function(x){ 
		$http({
			method:'GET',
			url:"/api/now/table/" + table,
			params:{
				sysparam_query:"active=true",
				sysparam_fields:"category, name",
				//sysparm_orderby: 'day' 
			}

		}).then(function(response){
			c.records = response.data.result;

			c.ProductsServices=[];
			c.customerSupport=[];
			c.quickInfo=[];
			c.ourCompany=[];
			c.workWithUs=[];

			for(var r = 0;r < c.records.length;r++){

				if(c.records[r].category == "products_&_services"){
					c.ProductsServices.push(c.records[r].name);
				}else if(c.records[r].category == "customer_support"){
					c.customerSupport.push(c.records[r].name);
				}else if(c.records[r].category == "quick_info"){
					c.quickInfo.push(c.records[r].name);
				}else if(c.records[r].category == "our_company"){
					c.ourCompany.push(c.records[r].name);
				}else if(c.records[r].category == "work_with_us"){
					c.workWithUs.push(c.records[r].name);
				}



			}//End FOR
		});//End THEN
	};

	c.fetchRecords();
};

