sp_widget.client_script:
api.controller = function($scope) {
    var c = this;

    c.updateAnimalRecord = function() {
        $scope.data.animalType = [1,2,3];
        console.log('Animal type set:', $scope.data.animalType); // Check if animalType is set
        $scope.server.update().then(function(response) {
            console.log('Server response:', response);
        }).catch(function(error) {
            console.error('Server update failed:', error);
        });
    };

    $scope.c.updateRecord = function() {
        c.updateAnimalRecord();
    };
};

sp_widget.script:
(function() {

	var animalType = input; // Use 'input' instead of 'data'
	gs.addErrorMessage(animalType);
	if (animalType == undefined) {
		gs.addErrorMessage('Error: Animal type not received');
	} else {


		gs.addInfoMessage('Animal type received: ' + animalType.animalType);
	}

})();
