sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	//Get current date & compare with employee of the month
	var today = new Date();
	var month = today.getMonth() + 1;
	var year = today.getFullYear();

	if(month < 10){
		month = '0' + month;	
	}
	var currentDate = year + '-' + month;
	var monthYear = month + '/' + year;
	//-------------------------------------

	//Populate UL 
	data.currentEmpOfMonth = {};
	data.pastEmpOfMonth = {};
	var num = 1;

	var grNews = new GlideRecord('x_1093559_hsc_em_0_bulletin');
	grNews.addQuery('display_location','employee_of_the_month');
	grNews.query();

	while(grNews.next()){		
		var empDate = grNews.getValue('employee_of_the_month').substring(0,7);
		var url = '/sys_attachment.do?sys_id=' + grNews.name.avatar;
		var empObject = {name:grNews.getDisplayValue('name'),url:url,date:empDate};
		var emp = 'emp' + num;

		if(currentDate == empDate){
			empObject.date = '';
			data.currentEmpOfMonth[emp] = empObject;
		}else{		
      empObject.date = monthYear;
			data.pastEmpOfMonth[emp] = empObject;
		}

		num++;
	}



})();
