sp_widget.client_script:
function($scope, $rootScope, spUtil) {
	// Initialize 'this' as 'c' to refer to the controller scope
	var c = this;

	// Set 'expanded' based on whether the user is on a mobile device
	// 'spUtil.isMobile()' returns true if the device is mobile, so we use '!' to set 'expanded' to true if not mobile
	c.expanded = !spUtil.isMobile();

	// Function to manually expand the section
	c.expand = function() {
		c.expanded = true; // Set 'expanded' to true when the function is called
	};

	// Register an event listener on the $rootScope for the event 'sp.update.breadcrumbs'
	// When this event is triggered, update the 'breadcrumbs' variable with the provided list
	var deregister = $rootScope.$on("sp.update.breadcrumbs", function(e, list) {
		//c.breadcrumbs = list; // Update 'breadcrumbs' with the new list

		c.breadcrumbs = [];

		for(i=list.length - 2; i < list.length  ;i++){
			c.breadcrumbs.push(list[i]);
		}

	});



	// Clean up the event listener when the scope is destroyed
	$scope.$on('$destroy', function(){
		deregister(); // Unregister the event listener to prevent memory leaks


	});

}

