sp_widget.client_script:
api.controller=function($scope) {
	/* widget controller */
	var c = this;

	//ng-click
	$scope.hello = function(){
		alert("hello ese vato!");
	}

	//ng-if must = true to show
	$scope.show = true;

	//ng-if coins
	$scope.coins = 10;

	//ng-model date
	var date = new Date();
	var month = date.getMonth();
	var day = date.getDate();
	var year = date.getFullYear();	
	var monthNameArray = 
			['January','February','March','April','May','June','July','August','September','October','November','December'];

	var daySuffix ='';
	switch(day){
		case 1:
		case 21:
		case 31:
			daySuffix = 'st';
			break;

		case 2:
		case 22:
			daySuffix = 'nd';
			break;

		case 3:
		case 23:
			daySuffix = 'rd';
			break;

		default:
			daySuffix = 'th';

	}

	var hour =	date.getHours();
	var minutes = date.getMinutes();
	var amPm = 'am';

	if(hour >= 12){
		hour = hour - 11;
		amPm = 'pm'
	}

	if(minutes < 10){
		minutes = '0' + minutes;
	}



	$scope.date1 = monthNameArray[month] + ' ' + day + daySuffix + ', ' + year;


	//ng-if
	var car = 'mustang';
	if(car == 'mustang'){
		$scope.wtf = true;
	}

};

