sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	//Populate UL 
	data.newsArray =[];
	var grNews = new GlideRecord('x_1093559_hsc_em_0_hsc_bulletin');
	grNews.addQuery('display_location','Other News');
	grNews.query();

	while(grNews.next()){
		var rec = grNews.getValue('description') + ' (' + grNews.getValue('sys_created_on') + ')';
		data.newsArray.push(rec);
	}



})();
