sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    // Define the UI Macro name
    var macroName = 'HSC truck';

    // Clear any existing icon to avoid duplicates
    var existingIcon = document.getElementById('truckIcon');
    if (existingIcon) {
        existingIcon.remove();
    }

    // Function to get the macro content
    function getMacroContent(macroName, callback) {
        var ga = new GlideAjax('GetMacroContent');
        ga.addParam('sysparm_name', macroName);
        ga.getXMLAnswer(callback);
    }

    // Callback function to handle the fetched macro content
    function handleMacroContent(response) {
        if (response) {
            var spanElement = document.createElement('span');
            spanElement.innerHTML = response;

            // Find the field element and insert the macro content next to it
            var fieldElement = g_form.getControl('short_description');
            if (fieldElement) {
                fieldElement.parentNode.insertBefore(spanElement, fieldElement.nextSibling);
            }
        }
    }

    // Fetch the macro content and handle it
    getMacroContent(macroName, handleMacroContent);
}
