sys_script_include.script:
var MyRestScriptInclude = Class.create();
MyRestScriptInclude.prototype = {
    initialize: function() {},

    getPosts: function() {
        var restMessage = new sn_ws.RESTMessageV2('myRestMessage', 'myHttpGetPosts');
        var response = restMessage.execute();
        var responseBody = response.getBody();
        var httpStatus = response.getStatusCode();

        if (httpStatus == 200) {
            var posts = JSON.parse(responseBody);
            this.storePosts(posts);
        } else {
            gs.info('Error with MyRestScriptInclude.');
        }
    },

    storePosts: function(posts) {
        posts.forEach(function(post) {
            var gr = new GlideRecord('x_1093559_hsc_em_0_hsc_rest_test');
            gr.initialize();
            gr.u_id = post.id;
            gr.u_title = post.title;
            gr.u_body = post.body;
            gr.insert();
        });
    },


    type: 'MyRestScriptInclude'
}
