sys_script_include.script:
var PublicAPIIntegration = Class.create();
PublicAPIIntegration.prototype = {
    initialize: function() {},

    getPosts: function() {
        var restMessage = new sn_ws.RESTMessageV2('Public API Integration', 'GET Posts');
        var response = restMessage.execute();
        var responseBody = response.getBody();
        var httpStatus = response.getStatusCode();

        if (httpStatus == 200) {  
            var posts = JSON.parse(responseBody);

            this.storePosts(posts);

        } else {  
            gs.error('Error fetching data: ' + httpStatus);
        }
    },

    storePosts: function(posts) { 
        posts.forEach(function(post) {
            var gr = new GlideRecord('x_1093559_hsc_em_0_u_public_api_data');
            gr.initialize();
            gr.u_id = post.id;
            gr.u_title = post.title;
            gr.u_body = post.body;
            gr.insert();

        });
    },

    type: 'PublicAPIIntegration'
};
