sys_script_include.script:
var UserProfileImage = Class.create();
UserProfileImage.prototype = {
    initialize: function() {
    },

    getUserImageUrl: function(userId) {
        var attachmentGr = new GlideRecord('sys_attachment');
        attachmentGr.addQuery('table_name', 'sys_user');
        attachmentGr.addQuery('table_sys_id', userId);
        attachmentGr.addQuery('content_type', 'image/jpeg'); // Adjust based on the image type
        attachmentGr.query();
        if (attachmentGr.next()) {
            return '/sys_attachment.do?sys_id=' + attachmentGr.sys_id;
        }
        return null;
    },

    type: 'UserProfileImage'
};
